/**
 * A state to select and edit shapes in a simple way. (kind of plug n play state)
 */
export default class SimpleEditionState extends BaseState {
  constructor(timeline) {
    super(timeline);

    this.currentEditedLayer = null;
    this.currentTarget = null;
  }

  enter() {}
  exit() {}

  handleEvent(e) {
    switch (e.type) {
      case 'mousedown':
        this.onMouseDown(e);
        break;
      case 'mousemove':
        this.onMouseMove(e);
        break;
      case 'mouseup':
        this.onMouseUp(e);
        break;
    }
  }

  onMouseDown(e) {
    // keep target consistent with mouse down
    this.currentTarget = e.target;

    this.layers.forEach((layer) => {
      if (!layer.hasElement(this.currentTarget)) { return; }

      if (!e.originalEvent.shiftKey) {
        layer.unselect();
      }

      const item = layer.getItemFromDOMElement(this.currentTarget);

      if (item === null) { return; }

      this.currentEditedLayer = layer;
      requestAnimationFrame(function() { layer.select(item); });
    });
  }

  onMouseMove(e) {
    if (!this.currentEditedLayer) { return; }

    const layer = this.currentEditedLayer;
    const items = layer.selectedItems;

    layer.edit(items, e.dx, e.dy, this.currentTarget);
    requestAnimationFrame(function() { layer.update(items); });
  }

  onMouseUp(e) {
    this.currentEditedLayer = null;
  }
}

/**
 * Formatting helpers functions.
 */
export default {
  /**
   * Add a `sign` to the left of a given `input` to match `length`
   *
   * @param {String} input - The string to format.
   * @param {String} sign - The character to add to the left.
   * @param {Number} length - The length of the output string.
   */
  padLeft(input, sign, length) {
    input += '';
    while (input.length < length) {
      input = sign + input;
    }
    return input;
  }
};

/**
 * OrthogonalData transforms an object of arrays `{foo: [1, 2], bar: [3, 4]}`
 * to or from an array of objects `[{foo: 1, bar: 3}, {foo: 2, bar: 4}]`
 */
export default class OrthogonalData {
  constructor() {
    this._cols = null; // Object of arrays
    this._rows = null; // Array of objects
  }

  /**
   * Check the consistency of the data.
   */
  _checkConsistency() {
    let size = null;

    for (let key in this._cols) {
      const col = this._cols[key];
      const colLength = col.length;

      if (size !== null && size !== colLength) {
        throw new Error(`${this.prototype.constructor.name}: inconsistent data`);
      } else if (size === null) {
        size = colLength;
      }
    }
  }

  /**
   * Updates array of objects from object of arrays.
   */
  updateFromCols() {
    let keys = Object.keys(this._cols);

    keys.forEach((key, i) => {
      const col = this._cols[key];

      col.forEach((value, index) => {
        if (this._rows[index] === undefined) this._rows[index] = {};
        this._rows[index][key] = value;
      });
    });

    this._checkConsistency();
  }

  /**
   * Updates object of arrays from array of objects.
   */
  updateFromRows() {
    this._rows.forEach((obj, index) => {
      for (let key in obj) {
        if (index === 0) this._cols[key] = [];
        this._cols[key].push(obj[key]);
      }
    });

    this._checkConsistency();
  }

  /**
   * Sets an object of arrays.
   *
   * @type {Object<String, Array>}
   */
  set cols(obj) {
    this._cols = obj;
    this._rows = [];

    this.updateFromCols();
  }

  /**
   * Returns an object of arrays.
   *
   * @type {Object<String, Array>}
   */
  get cols() {
    return this._cols;
  }

  /**
   * Sets an array of objects.
   *
   * @type {Array<Object>}
   */
  set rows(arr) {
    this._rows = arr;
    this._cols = {};

    this.updateFromRows();
  }

  /**
   * Returns an array of objects.
   *
   * @type {Array<Object>}
   */
  get rows() {
    return this._rows;
  }
}

/**
 * Lightweight scales mimicing the `d3.js` functionnal API.
 */
export default {
  /**
   * A linear scale interpolating values between a `domain` and a `range`.
   * @return {Function}
   */
  linear() {
    let _domain = [0, 1];
    let _range = [0, 1];

    let _slope = 1;
    let _intercept = 0;

    function _updateCoefs() {
      _slope = (_range[1] - _range[0]) / (_domain[1] - _domain[0]);
      _intercept = _range[0] - (_slope * _domain[0]);
    }

    function scale (value) {
      return (_slope * value) + _intercept;
    }

    scale.invert = function(value) {
      return (value - _intercept) / _slope;
    };

    scale.domain = function(arr = null) {
      if (arr === null) { return _domain; }

      _domain = arr;
      _updateCoefs();

      return scale;
    };

    scale.range = function(arr = null) {
      if (arr === null) { return _range; }

      _range = arr;
      _updateCoefs();

      return scale;
    };

    return scale;
  }
};

